function Task({task, archive, complete, del}){

    const handleDelete = () => {
        if (window.confirm("Confirmar deleção da tarefa?")) {
          del(task._id);
        }
    };

    return (
        <div className="row">
            <input type="checkbox"  checked = {task.completed} 
                                    onChange={() => complete(task._id)}></input>
            <span>{task.description}</span>
            <date>{task.end_date}</date>
            <button onClick = {handleDelete}>Apagar</button>
            {task.completed && (!task.archived) && <button onClick={() => archive(task._id)}>Arquivar</button>}
        </div>
    )
    
}

export default Task;
