//import logo from './logo.svg';
import './App.css';

import {useState, useEffect} from 'react'
import Task from './compenents/task' 

function App() {
  const path = "http://localhost:3000/agenda";
  const [tasks, setTasks] = useState([]);
  const [showArchived, setShowArchived] = useState(false);


  function getTasks(){ // not archived tasks 
    fetch(path + "/tasks", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function getArchived(){ // archived tasks 
    fetch(path + "/archive", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function createTask(item){ 
    fetch(path + "/task", {method: "POST", 
                            headers: {"Content-Type" : "application/json"},
                            body : JSON.stringify({"description": item.description, "end_date": item.end_date})})
    .then(response => response.json())
    //.then(data => getView(data));
  }

  function updateTask(id,item){ 
    fetch(path + "/task/" + id, {method: "PATCH", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"description": item.description, "end_date": item.end_date})})
    .then(response => response.json())
    //.then(data => getView(data));
  }

  function setArchive(id){ 
    fetch(path + "/task/" + id, {method: "PUT", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"action": "archive"})})
    .then(response => response.json())
    //.then(data => getView(data));
  }

  function setComplete(id){ 
    fetch(path + "/task/" + id, {method: "PUT", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"action": "complete"})})
    .then(response => response.json())
    //.then(data => getView(data));
  }

  function deleteTask(id){ 
    fetch(path + "/task/" + id, {method: "DELETE", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    //.then(data => getView(data));
  }

  function toggleTasks() {
    setShowArchived(!showArchived); 
  }

  useEffect(() => {
    if (!showArchived){
      getTasks();
   }else{
      getArchived();
   }
  },[showArchived]);

  return (
    <div className="wrapper">
      <h1>Agenda</h1>

      {tasks.map(task => {return (<Task key={task._id} 
                                        task = {task} 
                                        showArchived = {showArchived}
                                        archive = {setArchive}
                                        complete = {setComplete}
                                        />)})}

      <button onClick={toggleTasks}> {showArchived ? "Ver Ativas" : "Ver Arquivados"} </button>
      <button>New Task</button>

    </div>



  );
}

export default App;
