//import logo from './logo.svg';
import './App.css';

import {useState, useEffect} from 'react'
import Task from './compenents/task' 

function toggleTasks() {
  if (showArchived) {
    getTasks();      // Se estiver vendo arquivadas, muda para ativas
  } else {
    getArchived();   // Se estiver vendo ativas, muda para arquivadas
  }
  setShowArchived(!showArchived); // Atualiza o estado depois
}

function App() {
  const path = "http://localhost:3000/agenda";
  const [tasks, setTasks] = useState([]);

  function getTasks(){ // not archived tasks 
    fetch(path + "/tasks", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  function getArchived(){ // archived tasks 
    fetch(path + "/archive", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function createTask(item){ 
    fetch(path + "/tasks", {method: "POST", 
                            headers: {"Content-Type" : "application/json"},
                            body : JSON.stringify({"description": item.description, "end_date": item.end_date})})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function updateTask(id,item){ 
    fetch(path + "/tasks/" + id, {method: "PATCH", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"description": item.description, "end_date": item.end_date})})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function setAction(id,action){ 
    fetch(path + "/tasks/" + id, {method: "PUT", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"action": action})})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function deleteTask(id){ 
    fetch(path + "/tasks/" + id, {method: "DELETE", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  useEffect(() => {getTasks()},[]);

  return (
    <div className="wrapper">
      <h1>Agenda</h1>

      {tasks.map(task => {return (<Task key={task._id} 
                                        task = {task} 
                                        update = {updateTask}
                                        action = {setAction}
                                  />)})}

      <button>ativos/arquivados</button>
      <button>New Task</button>

    </div>



  );
}

export default App;
