function Task({task, showArchived, archive, complete}){
    //setAction(id,action)
    return (
        <div className="row">
            <input type="checkbox" checked = {task.completed} onclick = {() => complete(task._id)}></input>
            <span>{task.description}</span>
            <button>Apagar</button>
            <button onclick = {() => archive(task._id, "archive")}>
                {showArchived ? "Ativar" : "Arquivar"}
            </button>
        </div>
    )
    
}



export default Task;
