//import logo from './logo.svg';
import './App.css';

import {useState, useEffect} from 'react'
import Task from './compenents/task' 

function App() {
  const path = "http://localhost:3000/agenda";
  const [tasks, setTasks] = useState([]);

  function getTasks(){ // not archived tasks 
    fetch(path + "/tasks", {method: "GET"}).then(response => response.json())
    .then(data => setTasks(data));
  }

  function getArchived(){ // archived tasks 
    fetch(path + "/archive", {method: "GET"}).then(response => response.json())
    .then(data => setTasks(data));
  }

  function createTask(item){ 
    fetch(path + "/archive", {method: "POST", body : {description: item.description, }})
    .then(response => response.json())
    .then(data => setTasks(data));
  }


  function deleteTask(id){ 
    fetch(path + "/" + id, {method: "DELETE"}).then(response => response.json())
    .then(data => setTasks(data));
  }



  useEffect(() => {getTasks()},[]);

  return (
    <div className="wrapper">
      <h1>Agenda</h1>

      {tasks.map(task => {return (<Task key={task._id} task = {task}/>)})}

      <button>ativos/arquivados</button>
      <button>New Task</button>

    </div>



  );
}

export default App;
