//import logo from './logo.svg';
import './App.css';

import {useState, useEffect} from 'react'
import Task from './compenents/task' 

function App() {
  const path = "http://localhost:3000/agenda";
  const [tasks, setTasks] = useState([]);
  const [newTaskVisible, setNewTaskVisible] = useState(false); 
  const [newDescription, setNewDescription] = useState('');
  const [newDate, setNewDate] = useState('');

  const [searchQuery, setSearchQuery] = useState('');

  function getTasks(){ // not archived tasks 
    fetch(path + "/tasks", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function getArchived(){ // archived tasks 
    fetch(path + "/archive", {method: "GET", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => setTasks(data));
  }

  function createTask(description, date){ 
    fetch(path + "/task", {method: "POST", 
                            headers: {"Content-Type" : "application/json"},
                            body : JSON.stringify({"description": description, "end_date": date})})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  function updateTask(id, description, date){ 
    fetch(path + "/task/" + id, {method: "PATCH", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"description": description, "end_date": date})})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  function setArchive(id){ 
    fetch(path + "/task/" + id, {method: "PUT", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"action": "archive"})})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  function setComplete(id){ 
    fetch(path + "/task/" + id, {method: "PUT", 
                                  headers: {"Content-Type" : "application/json"},
                                  body : JSON.stringify({"action": "complete"})})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  function deleteTask(id){ 
    fetch(path + "/task/" + id, {method: "DELETE", headers: {"Content-Type" : "application/json"}})
    .then(response => response.json())
    .then(data => getTasks(data));
  }

  useEffect(() => {
      getTasks();
  },[]);

  const handleSaveNewTask = () => {
    if (newDescription && newDate) {
      createTask(newDescription, newDate);
      setNewDate('');
      setNewDescription('');
      setNewTaskVisible(false);

    } else {
      alert("Preencha todos os campos.");
    }
  };

  const handleSearch = () => {
    if (window.confirm("Confirmar pesquisa?")) {
      const filtered = tasks.filter(task => task.description.toLowerCase().includes(searchQuery.toLowerCase()));
      setFilteredTasks(filtered);
    }
  };

  return (
    <div className="wrapper">
      <h1>Agenda</h1>
      <button>Arquivadas</button>
      {tasks.map(task => {return (<Task key={task._id} 
                                        task = {task} 
                                        archive = {setArchive}
                                        complete = {setComplete}
                                        del = {deleteTask}
                                        update = {updateTask}
                                        />)})}

      <button onClick={() => setNewTaskVisible(true)}>Nova Tarefa</button>

      {newTaskVisible && (
        <div className="new-task-form">
          <input  type="text" placeholder="Descrição" value={newDescription}
                  onChange={(e) => setNewDescription(e.target.value)}
          />
          <input type="date" value={newDate}
            onChange={(e) => setNewDate(e.target.value)}
          />
          <button onClick={handleSaveNewTask}>Salvar</button>
          <button onClick={() => {setNewTaskVisible(false);setNewDescription('');setNewDate('');
                          }}>Cancelar</button>
        </div>
      )}

    </div>



  );
}

export default App;
